import React from 'react';
import {useState} from 'react';

const Bombilla = () => {

    const[isOn, setIsOn]=useState(false);
    const luz=()=> setIsOn(!isOn);
  
    return (
        <div>
        <div id="bombilla" style={{backgroundColor:"yellow" }} ></div>
        <button onClick={luz}>{isOn ? 'on':'off'}</button>
        </div>
    );
};

export default Bombilla;
