import React, { useState, useEffect } from 'react';
import axios  from 'axios';

const Clima = () => {
    const conversorKC=(kelvin,grado)=>[Math.round(kelvin - 273.15),grado];
    const conversorKF=(kelvin,grado)=>[Math.round((kelvin - 273.15) * 9/5 + 32 ),grado];
    




    const [clima,setClima]=useState(null);
    const [tempe,setTempe]=useState([0,"°C"]);
    useEffect(()=>{
        let error=()=>console.log("Error");

        let exito=posicion=>{
        let lat= posicion.coords.latitude;
        let lon= posicion.coords.longitude;
        axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=307298b190f88060b9d58d5a9e6d892b`)
        .then(res=>setClima(res.data));
        }
        
        navigator.geolocation.getCurrentPosition(exito,error);
       /*
       if(tony===true){
           hacer algo
       }else{
           otra cosa
       } 
       
       tony===true? hacer algo : otra cosa;
       
       
       */

    },[]);
    
    const conversor=()=>{
        if(tempe[1]==="°C"){
            setTempe(conversorKF(clima?.main.temp,"°F"))
        }else{
            setTempe(conversorKC(clima?.main.temp,"°C"))
        }
    }
    
    console.log(clima);
    return (
       
        <div className="fondo" style={{backgroundImage:"./noche.gif"}}>
            <div className='tarjeta'>
                <h1>País: {clima?.sys.country}</h1>
                <h2>Ciudad: {clima?.name}</h2>
                <img src={`http://openweathermap.org/img/wn/${clima?.weather[0].icon}@2x.png`}/>
                <p >Temperatura: {conversor?tempe:conversorKC(clima?.main.temp,"°C")} </p>
                <p>Visibilidad: {clima?.visibility} metros</p>
                <button onClick={conversor}>{tempe[1]==="°C"?"Fahrenheit":"Celsius"}</button>  
             </div>
         </div>
    );
};

export default Clima;
