import React, { useState, useEffect } from 'react';
import axios  from 'axios';

const Clima = () => {
    const conversorKC=kelvin=>Math.round(kelvin - 273.15);
    const conversorKF=kelvin=>Math.round((kelvin - 273.15) * 9/5 + 32 );
    
    const [clima,setClima]=useState(null);
    const [tempe,setTempe]=useState("");
    useEffect(()=>{
        let error=()=>console.log("Error");

        let exito=posicion=>{
        let lat= posicion.coords.latitude;
        let lon= posicion.coords.longitude;
        axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=307298b190f88060b9d58d5a9e6d892b`)
        .then(res=>setClima(res.data));
        }
        
        navigator.geolocation.getCurrentPosition(exito,error);
        

    },[]);
    
    
    console.log(clima);
    return (
       
        <div className="fondo" >
            <div className='tarjeta'>
                <h1>Pa√≠s: {clima.sys?.country}</h1>
                <h2>Ciudad: {clima.name}</h2>
                <i><img src={`http://openweathermap.org/img/wn/${clima.weather[0].icon}@2x.png`}/></i>
                <p >Temperatura: {tempe} grados Centigrados</p>
                <p>Visibilidad: {clima.visibility} metros</p>
                <button >Farenheit</button>  
             </div>
         </div>
    );
};

export default Clima;
